export class FilepondFileSizeValidator{static tags=[{event:"contao_filepond:process_start",priority:100}];handle(e){const i=e.file,l=e.filepondOptions;if(!0!==l.allowFileSizeValidation)return;const r=Number(l.minFileSize)||0,o=Number(l.maxFileSize)||0;if(o&&i.size>o){const e={filesize:i.size,maxFileSize:o},r=l.labelMaxFileSizeError.replace(/\{(\w+)\}/g,(i,l)=>e[l]);throw new Error(r)}if(r&&i.size<r){const e={filesize:i.size,minFileSize:r},o=l.labelMinFileSizeError.replace(/\{(\w+)\}/g,(i,l)=>e[l]);throw new Error(o)}}}